import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    card: {

      borderRadius: 16,
      padding: 10,
      marginHorizontal: 16,
      marginVertical: 20,
      width: 330,
      textAlign: "center",
      paddingTop: 25,
      flexDirection: "row"
    },
    title: {
      fontSize: 18,
      marginBottom: 8,
    },
    description: {
      fontSize: 15,
      padding: 5,
      paddingRight: 5,
      fontWeight: "bold",
      color: "#ffffff"

    },
  });

  export default styles;
