import React from 'react';
import { StyleSheet, View, Text, Image, ScrollView } from 'react-native';
import MiniCardHorizantal from './minicardhorizontal';
import { Entypo } from '@expo/vector-icons'; 
//import styles from './styles';
import {getColorCard} from '../../screens/api/api';



const Cardbottom = (props) => {

  const color = getColorCard(props.color);

  return (

    <ScrollView>
      <View style={styles.card}>
        <View style={{ flex: 1, flexDirection: "row" }}>
          <Text style={styles.description}>NextForecast</Text>
          <Entypo name="calendar" size={24} color="#ffffff" />
        </View>
       


{props.data.map((nome, index) => {
        if (index != 0) {
          return (
            <MiniCardHorizantal  day={nome.weekday} max={nome.max} min={nome.min} condition={nome.condition}></MiniCardHorizantal>
          );
        }
        return null;
      })} 


      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: color,
    borderRadius: 25,
    elevation: 3,
    shadowColor: '#ffffff',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 1,
    padding: 10,
    marginHorizontal: 16,
    marginVertical: 2,
    width: 330,
    flex: 1,


  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  description: {
    fontSize: 18,
    paddingRight: 170,
    paddingTop: 0,
    color: '#ffffff',

  },
  description1: {
    fontSize: 18,
    paddingRight: 56,
    paddingTop: 20,
    color: '#ffffff',

  },
  description2: {
    fontSize: 20,
    paddingLeft: 49,
    paddingTop: 20,
    color: '#ffffff'
  }
});


export default Cardbottom;
