import { StyleSheet } from 'react-native';
import {getColor} from '../../screens/api/api';
import {getColorCard} from '../../screens/api/api';


const styles = StyleSheet.create({
    card: {
      backgroundColor: '#104084',
      borderRadius: 16,
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.2,
      shadowRadius: 0,
      padding: 12,
      marginHorizontal: 4,
      marginVertical: 2,
      width:110,
      height:140,
      paddingTop:30,
     alignItems:"center",
     marginLeft:21
    },
    title: {
      fontSize: 18,
      fontWeight: 'bold',
      marginBottom: 8,
    },
    description: {
      fontSize: 17,
      color:'#ffffff',
    },
    image: {
      width: 60, 
      height: 60
    },
  });

  export default styles;
