import React from 'react';
import { StyleSheet, View, Text,Image } from 'react-native';
import Svg, { Path } from 'react-native-svg';
const MiniCardHorizontal = (props) => {
  return (

     <View style={{flex:1,flexDirection:"row"}}>

     <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
     <Text style={styles.description1}>{props.day}</Text> 
    </View>

    <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
    {/* <Image
       source={require('../assets/solnublado.png')}  
       style={{width: 30,  height: 30, padding:22,margin:6}} 
   /> */}
   <Svg height="100" width="100">
        <Path
          d="M48.5 54.6c-5.8 0-10.5 4.8-10.5 10.7C38 71 42.5 75.8 48 76h28.2c4.3-.2 7.8-4 7.8-8.3 0-4.6-3.7-8.4-8.2-8.4h-.5c-1-5.6-5.5-10-11.2-10.3h-.7c-4.5 0-8.5 2.6-10.6 6.4-1.3-.5-2.7-.8-4.2-.8z"
          fill="red"
          stroke="blue"
          strokeWidth="3"
        />
      </Svg>
    </View>
    <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
    <Text style={styles.description2}>{props.min}ยบ - {props.max}ยบ</Text> 
    </View>



     </View>

  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#104084',
    borderRadius: 16,
    shadowColor: '#ffffff',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 1,
    padding: 5,
    width:330,
    height:180
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
  description: {
    fontSize: 18,
    paddingRight:30,
    paddingLeft:0,
    paddingTop:0,
    color:'#ffffff'
  },
  description1: {
    fontSize: 18,
    paddingRight:60,
    paddingTop:20,
    color:'#ffffff'
  },
  description2: {
    fontSize: 18,
    paddingLeft:20,
    paddingTop:20,
    color:'#ffffff'
  }
});

export default MiniCardHorizontal;
