import React from 'react';
import {  View, Text } from 'react-native';
import { Image } from 'react-native-elements';
import styles from './stylesMiniCard';

const MiniCardHorizontal = (props) => {

  switch (props.condition) {
    case "rain":
      var imagem = require('../../assets/rain.png');
      break;
    case "cloud":
      var imagem = require('../../assets/cloud.png');
      break;
    case "clear_day":
      var imagem = require('../../assets/clear_day.png');
      break;
    case "clear_night":
      var imagem = require('../../assets/clear_night.png');
      break;
    case "cloudly_day":
      var imagem = require('../../assets/cloudly_day.png');
      break;
    case "cloudly_night":
      var imagem = require('../../assets/cloudly_night.png');
      break;
    case "snow":
      var imagem = require('../../assets/snow.png');
      break;
    case "fog":
      var imagem = require('../../assets/fog.png');
      break;
    case "hail":
      var imagem = require('../../assets/hail.png');
      break;
    case "storm":
      var imagem = require('../../assets/storm.png');
      break;

    default:
      var imagem = require('../../assets/cloudly_day.png');
      break;
  }


  return (

    <View style={styles.card} >
      
      <View style={{ width: "100%", fekx: 1, flexDirection: "row" }}>
        <View style={{ width: "40%" }}><Text style={styles.description1}>NextForecast</Text></View>
        <View style={{ width: "20%" }}><Text style={styles.description2}> TT</Text></View>
        <View style={{ width: "40%" }}><Text style={styles.description3}> TT</Text></View>

      </View>
      
      {/* <View style={styles.elements}>
        <Text style={styles.description1}>{props.day}</Text>
      </View>

      <View style={[styles.elements, { paddingLeft: 13, paddingRight: 10 }]}>
        <Image
          source={imagem}
          style={styles.imageCard}
        />
      </View>
      <View style={styles.elements}>
        <Text style={styles.description2}>{props.min}ยบ - {props.max}ยบ</Text>
      </View> */}

    </View>

  );
};


export default MiniCardHorizontal;
