import React, { Component } from 'react'
import { View, StyleSheet ,Text} from 'react-native'
  
const App = (props) => {
   return (
      <View style = {styles.container}>
         <View style = 
               {[styles.item,{backgroundColor:'green'}]} >  <Text >Today</Text>  </View>
         <View style = 
               {[styles.item,{backgroundColor:'blue'}]} >  <Text >Today</Text>  </View>
         <View style = 
               {[styles.item,{backgroundColor:'red'}]} >  <Text >Today</Text>  </View>
      </View>
   )
}
export default App;
  
const styles = StyleSheet.create ({
   container: {
      flexDirection: 'row',
      justifyContent: 'flex-start',
      marginTop:10,
      height: 100
   },
   item:{
     width:120,
     height:120
   }
