import React from 'react';
import { StyleSheet, View, Text, Image, ScrollView } from 'react-native';
import MiniCardHorizantal from './minicardhorizontal';
import { Entypo } from '@expo/vector-icons';
import styles from './styles';
import { getColorCard } from '../../screens/api/api';

const Cardbottom = (props) => {

  var color =  props.color;

  return (

    <ScrollView style={[styles.card, {backgroundColor:color}]}>
      
      <View style={[styles.card, {backgroundColor:color}]}> 
     <View style={{flex:1,flexDirection:"row"}}>
         <Text style={styles.description}>Today</Text>
         <Text style={styles.description1}>{props.day}</Text>
     </View>
     <View style={{flexDirection:"row"}}>
     <View style={{flexDirection:"row", marginLeft:35, alignItems:"center"}}>
         <MiniCard description='sunrise' imagem="sunrise" hour={props.sunrise}></MiniCard>
         <MiniCard description='sunset' imagem="sunset" hour={props.sunset}></MiniCard>
     </View>
     </View>
    </View>
      
     
    </ScrollView>
  );
};



export default Cardbottom;
