export const getImage =  (image) => {
    switch (image) {
        case "rain":
          var imagem = ('../../assets/rain.png');
          break;
        case "cloud":
          var imagem = ('../../assets/cloud.png');
          break;
        case "clear_day":
          var imagem = ('../../assets/clear_day.png');
          break;
        case "clear_night":
          var imagem = ('../../assets/clear_night.png');
          break;
        case "cloudly_day":
          var imagem = ('../../assets/cloudly_day.png');
          break;
        case "cloudly_night":
          var imagem = ('../../assets/cloudly_night.png');
          break;
        case "snow":
          var imagem = ('../../assets/snow.png');
          break;
        case "fog":
          var imagem = ('../../assets/fog.png');
          break;
        case "hail":
          var imagem = ('../../assets/hail.png');
          break;
        case "storm":
          var imagem = ('../../assets/storm.png');
          break;

        default:
          var imagem = ('../../assets/cloudly_day.png');
          break;
      }
      return imagem;
  }
