const MiniCardHorizontal = (props) => {
  return (

     <View style={{flex:1,flexDirection:"row"}}>

     <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
     <Text style={styles.description1}>{props.day}</Text> 
    </View>

    <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
    {/* <Image
       source={require('../assets/solnublado.png')}  
       style={{width: 30,  height: 30, padding:22,margin:6}} 
   /> */}
   <Svg height="100" width="100">
        <Path
          d="M17.3 47C7.8 47 0 39 0 29.6c0-1.7.3-3.4.7-5 2-7 8.3-12 15.8-12.4C20.3 4.8 28 0 36.2 0 46.7 0 55.8 7.5 58 17.7c7.3 1 13 7 13 14.8 0 8-6.4 14.5-14.5 14.5H17.3z"
          fill="#F5F6F6" stroke="#BBC0C4" stroke-width="3"
        />
      </Svg>
    </View>
    <View style={{flex:1,flexDirection:"row",alignItems:"center"}}>
    <Text style={styles.description2}>{props.min}ยบ - {props.max}ยบ</Text> 
    </View>



     </View>

  );
};

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#104084',
    borderRadius: 16,
    shadowColor: '#ffffff',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 1,
    padding: 5,
    width:330,
    height:180
  },
  title: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 8,
  },
