import { StyleSheet } from 'react-native';


const styles = StyleSheet.create({
    card: {
      paddingLeft: 10,
      flex: 1,
      width: '100%',
      flexDirection: "row",
      justifyContent: "space-between"

    },
    elements: { 
      flex: 1, 
      flexDirection: "row", 
      alignItems: "center" 
    },
    imageCard: {
      width: 40, 
      height: 40, 
      margin: 12 
    },
    description1: {
      fontSize: 18,
      paddingLeft: 30,
      paddingLeft: 0,
      textAlign: 'left',
      color: '#ffffff'
    },
    description2: {
      fontSize: 18,
      paddingLeft: 15,
      paddingTop: 0,
      textAlign: 'center',
      color: '#ffffff'
    },
    description3: {
      fontSize: 18,
      paddingRight: 15,
      paddingTop: 0,
      textAlign: 'right',
      color: '#ffffff'
    }
  });

  export default styles;
