import React,{useState} from 'react';
import { StyleSheet, View, Text } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons'; 
import { MaterialCommunityIcons } from '@expo/vector-icons'; 
import { AntDesign } from '@expo/vector-icons';
import { TextInput,  Modal, TouchableOpacity } from 'react-native'; 
import { SearchBar } from 'react-native-elements';



const CardTop = (props) => {

const [search, setSearch] = useState();
const [visible, setVisible] = useState(true);
const handleCancel = () => {
  if (visible) {
    setVisible(false);
  } else {
    setVisible(true);
  }

};
const handleSearch = () => {
  setrefresh(Math.random);
};

  return (



    <View style={styles.card}>
     

     {!visible && (
          <View style={{ width: '100%' }}>
            <SearchBar
              placeholder="Procure uma cidade..."
              value={search}
              onChangeText={text => setSearch(text)}
              containerStyle={{ backgroundColor: 'transparent', borderBottomColor: 'transparent', borderTopColor: 'transparent', padding: 40 }}
              inputContainerStyle={{ backgroundColor: '#fff' }}
              searchIcon={{ color: '#000' }}
              clearIcon={{ color: '#000', onPress: handleCancel }}
              onSubmitEditing={handleSearch}
            />
          </View>
        )}

        {visible && (
          <TouchableOpacity onPress={handleCancel}>
            <Header city={search}></Header>
          </TouchableOpacity>
        )}
      
      
    </View>
  );
};

const styles = StyleSheet.create({
  card: {
  
    borderRadius: 16,
    padding: 10,
    marginHorizontal: 16,
    marginVertical: 8,
    width:330,
    textAlign:"center",
    paddingTop:25,
    flexDirection:"row"
    
    
  },
  title: {
    fontSize: 18,
    marginBottom: 8,
  },
  description: {
    fontSize: 15,
    padding:10,
    paddingRight:5,
    fontWeight:"bold",
    color:"#ffffff"
    
  },
});

export default CardTop;
