import React, { useEffect, useState } from 'react';
import { StyleSheet, TouchableOpacity, Text, View, ScrollView } from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import { Ionicons } from '@expo/vector-icons';
import CardTop from '../../components/card1/card1';
import CardMiddle from '../../components/card2/card2';
import CardBottom from '../../components/card3/card3';
import Header from '../../components/header/header';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import axios from 'axios';
import { getResults, getTodayResults } from '../api/api';
import { getImage } from '../utils/utils';
import { SearchBar, Image } from 'react-native-elements';
import styles from './styles';
import { parseISO, isAfter, format, formatRelative, formatDistance } from 'date-fns';


export default function App() {

  const [results, setResults] = useState();
  const [temp, setTemp] = useState();
  const [date, setDate] = useState();
  const [time, setTime] = useState();
  const [sunrise, setSunrise] = useState();
  const [sunset, setSunset] = useState();
  const [description, setDescription] = useState();
  const [currently, setCurrently] = useState();
  const [city, setCity] = useState();
  const [humidity, setHumidity] = useState();
  const [forecast, setForecast] = useState([]);
  const [windspeedy, setWindspeedy] = useState();
  const [search, setSearch] = useState('Gramado');
  const [refresh, setrefresh] = useState();
  const [visible, setVisible] = useState(true);

  const datetoName = (data) => {
    const dat = data.substr(6, 4) + '-' + data.substr(3, 2) + '-' + data.substr(0, 2);
    const firstDate = parseISO(dat);
    const month = format(firstDate, "MMM");
    const day = format(firstDate, "d");
    setDate(month + ',' + day);
  };

  const handleCancel = () => {
    if (visible) {
      setVisible(false);
    } else {
      setVisible(true);
    }

  };
  const handleSearch = () => {
    setrefresh(Math.random);
  };

  useEffect(() => {
    getResults(search).then((response) => {
      setResults(response.results);
      setTemp(response.results.temp);
      setHumidity(response.results.humidity);
      setWindspeedy(response.results.wind_speedy);
      setForecast(response.results.forecast);
      setCity(response.results.city);
      datetoName(response.results.date);
      setSunrise(response.results.sunrise);
      setSunset(response.results.sunset);
    });

    getTodayResults(search).then((response) => {
      setForecast(response);
    });


  }, [refresh])



  const colors = ["#08244F", "#134CB5", "#0B42AB"];

  return (
    <ScrollView>
      <View style={styles.container}>
        <LinearGradient
          colors={colors}
          style={styles.background}
        />

        {!visible && (
          <View style={{ width: '100%' }}>
            <SearchBar
              placeholder="Procure uma cidade..."
              value={search}
              onChangeText={text => setSearch(text)}
              containerStyle={{ backgroundColor: 'transparent', borderBottomColor: 'transparent', borderTopColor: 'transparent', padding: 40 }}
              inputContainerStyle={{ backgroundColor: '#fff' }}
              searchIcon={{ color: '#000' }}
              clearIcon={{ color: '#000', onPress: handleCancel }}
              onSubmitEditing={handleSearch}
            />
          </View>
        )}

        {visible && (
          <TouchableOpacity onPress={handleCancel}>
            <Header city={search}></Header>
          </TouchableOpacity>
        )}

        {forecast.map((item, index) => {
          if (index === 0) {
            const imagem = getImage(item.condition);


            return (
              <View style={{ alignItems: 'center', justifyContent: 'center' }}>
                <Image
                  source={imagem}
                  style={{ width: 180, height: 180 }}
                />

                <Text style={{ fontSize: 80, color: "#ffffff" }}>{results.temp}º</Text>
                <Text style={{ fontSize: 18, color: "#ffffff" }}>{forecast.description}</Text>
                <Text style={{ fontSize: 18, color: "#ffffff" }}>Max: {forecast.max} º  Min: {item.min}º </Text>
              </View>
            );
          }
          return null;
        })}

        <View ></View>

        <CardTop description="90%" humidity={humidity} windspeedy={windspeedy}></CardTop>
        <CardMiddle day={date} sunrise={sunrise} sunset={sunset}></CardMiddle>
        <CardBottom data={forecast} ></CardBottom>

      </View>
    </ScrollView>
  );
}
